import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Educacion } from 'src/app/model/educacion';
import { EducacionService } from 'src/app/service/educacion.service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-educacion',
  templateUrl: './educacion.component.html',
  styleUrls: ['./educacion.component.css']
})
export class EducacionComponent implements OnInit {

  educacion: Educacion[] = [];
  nombreE!: string;
  descripcionE!: string;
  finalE!: string;

  constructor(private educacionS: EducacionService, private tokenService: TokenService, private router: Router) { }

  isLogged = false;


  ngOnInit(): void {

    this.cargarEducacion();
    if(this.tokenService.getToken()){
      this.isLogged = true;      
    } else {
      this.isLogged = false;
    }

  }

  cargarEducacion(): void {
    this.educacionS.lista().subscribe(data =>{this.educacion = data;})
  }

  delete(id: number){
    if(id != undefined){
      this.educacionS.delete(id).subscribe(data => {this.cargarEducacion()}, 
      err => {
        alert("No se pudo eliminar item de educacion"); }
      )
    }
  }

  onCreate(): void{
    const educacion = new Educacion(this.nombreE, this.descripcionE, this.finalE);
    this.educacionS.save(educacion).subscribe(data =>{
      alert("Educacion creada correctamente");
      this.router.navigate(['']);
    }, err =>{
      alert("Fallo la creacion de nueva educacion");
      this.router.navigate(['']);
    })
  }


}
<div class="card mat-card" id="educ">  
    <div id="education" class="border">
      <div class="card-titles">
        <h2 class="card-header">Educación</h2>
        <div class="btn-edit">
          <button type="button" class="btn btn-right" data-toggle="modal" data-target="#myModalEduc" *ngIf="isLogged">
          <i class="fa fa-plus"></i>
          </button>
        </div>      
      </div> 


      <div class="col-lg-12">
       <table class="table">
        <tbody>
        <tr class="items-list" id="eduList" *ngFor="let Educacion of educacion">
          <td>
            <h5>Institucion: </h5>
            <p>{{Educacion.nombreE}}</p>                    
          </td>
          <td>
            <h5>Titulo: </h5>
            <p>{{Educacion.descripcionE}}</p>
          </td>
          <td>
            <h5>Periodo: </h5>
            <p>{{Educacion.finalE}}</p>        
          </td>
          <td class="btn-edit" *ngIf="isLogged">
            <button type="button" class="btn btn-right" routerLink="/editedu/{{Educacion.id}}">
            <i class="bi bi-pencil-fill"></i></button>
          </td>
          <td class="btn-edit" *ngIf="isLogged">
            <button class="btn btn-right" (click)="delete(Educacion.id)">
            <i class="bi bi-trash-fill"></i></button>   
          </td>           
        </tr>
      </tbody>              


      </table>
     </div>


    </div>



  </div> 




  <!--Modal NewEducacion-->
  <div class="modal fade" id="myModalEduc" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog" role="document">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="myModalEduc">Agregar Educacion</h5>
          <button type="button" class="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div class="modal-body">
          <form (ngSubmit)="onCreate()" novalidate #f="ngForm">
            <div class="form-group">
                <label for="nombreE">Nombre de la institucion: </label>
                <input type="text" class="form-control" id="nombreE" name="nombreE" [(ngModel)]="nombreE" required>
            </div>
            <div class="form-group">
                <label for="descripcionE">Titulo: </label>
                <input type="text" class="form-control" id="descripcionE" name="descripcionE" [(ngModel)]="descripcionE" required>
            </div>
            <div class="form-group">
              <label for="finalE">Periodo: </label>
              <input type="text" class="form-control" id="finalE" name="finalE" [(ngModel)]="finalE" required>
          </div>

            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-dismiss="modal" onclick="location.reload()">Cerrar</button>
              <button type="submit" class="btn btn-light" onclick="location.reload()">Agregar Educacion</button>
            </div>         
          </form>
        </div>      
      </div>
    </div>
  </div>
