import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Proyecto } from 'src/app/model/proyecto';
import { ProyectoService } from 'src/app/service/proyecto.service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-proyectos',
  templateUrl: './proyectos.component.html',
  styleUrls: ['./proyectos.component.css']
})
export class ProyectosComponent implements OnInit {

  proyecto: Proyecto[] = [];
  nombre!: string;
  descripcion!: string;
  fecha!: string;
  link!: string;

  constructor(private proyectoS: ProyectoService, private tokenService: TokenService, private router: Router) { }

  isLogged = false;



  ngOnInit(): void {
    this.cargarProyecto();
    if(this.tokenService.getToken()){
      this.isLogged = true;      
    } else {
      this.isLogged = false;
    }
  }

  cargarProyecto(): void{
    this.proyectoS.lista().subscribe(data =>{this.proyecto = data});
  }

  delete(id: number){
    if(id != undefined){
      this.proyectoS.delete(id).subscribe(data => {this.cargarProyecto()}, 
      err => {
        alert("No se pudo eliminar item de proyecto"); }
      )
    }
  }

  onCreate(): void{
    const proyecto = new Proyecto (this.nombre, this.descripcion, this.fecha, this.link);
    this.proyectoS.save(proyecto).subscribe(data =>{
      alert("Proyecto creado correctamente");
      this.router.navigate(['']);
    }, err =>{
      alert("Fallo la creacion de nuevo proyecto");
      this.router.navigate(['']);
    })
  }


}
<div class="card mat-card">
    <div class="card-titles">
        <h2 class="card-header">Proyectos</h2>
        <div class="btn-edit">
          <button type="button" class="btn btn-right" data-toggle="modal" data-target="#myModalProyect" *ngIf="isLogged">
          <i class="fa fa-plus"></i>
          </button>
        </div>  
    </div>
    <div class="col-lg-12">
      <table class="table">
       <tbody>
        <tr class="items-list" *ngFor="let Proyecto of proyecto">
          <td>
            <h5>Nombre del proyecto: </h5>
            <p>{{Proyecto.nombre}}</p>                    
          </td>
          <td>
            <h5>Descripcion: </h5>
            <p>{{Proyecto.descripcion}}</p>
          </td>
          <td>
            <h5>Fecha de realizacion: </h5>
            <p>{{Proyecto.fecha}}</p>        
          </td>
          <td>
           <h5>Link del proyecto: </h5>
           <p><a href="{{Proyecto.link}}">Haz click aqui</a></p>        
          </td>
          <td class="btn-edit" *ngIf="isLogged">
            <button type="button" class="btn btn-right" routerLink="/editproyecto/{{Proyecto.id}}">
            <i class="bi bi-pencil-fill"></i></button>
          </td>
          <td class="btn-edit" *ngIf="isLogged">
            <button class="btn btn-right" (click)="delete(Proyecto.id)">
            <i class="bi bi-trash-fill"></i></button>   
          </td>           
        </tr>
       </tbody>              


      </table>
    </div>     

</div> 


<!--Modal NewProyecto-->
<div class="modal fade" id="myModalProyect" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="myModalProyect">Agregar Proyecto</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <form (ngSubmit)="onCreate()" novalidate #f="ngForm">
          <div class="form-group">
              <label for="nombre">Nombre del proyecto: </label>
              <input type="text" class="form-control" id="nombre" name="nombre" [(ngModel)]="nombre" required>
          </div>
          <div class="form-group">
            <label for="fecha">Fecha de realizacion: </label>
            <input type="text" class="form-control" id="fecha" name="fecha" [(ngModel)]="fecha" required>
        </div>
          <div class="form-group">
              <label for="descripcion">Descripcion: </label>
              <input type="text" class="form-control" id="descripcion" name="descripcion" [(ngModel)]="descripcion" required>
          </div>
          <div class="form-group">
            <label for="link">Link del proyecto: </label>
            <input type="text" class="form-control" id="link" name="link" [(ngModel)]="link" required>
        </div>

          <div class="modal-footer">
            <button type="button" class="btn btn-secondary" data-dismiss="modal" onclick="location.reload()">Cerrar</button>
            <button type="submit" class="btn btn-primary" onclick="location.reload()">Agregar Proyecto</button>
          </div>         
        </form>
      </div>      
    </div>
  </div>
</div>
